import { useDispatch } from "react-redux";
import classes from "./CartItem.module.css";
import { cartActions } from "../../store/cart-slice";

const CartItem = (props) => {
  const { title: name, quantity, total: totalPrice, price, id } = props.item;
  const dispatch = useDispatch();

  const toggleIncrease = () => {
    dispatch(
      cartActions.addItemToCart({ id, price, quantity, totalPrice, name })
    );
  };
  const toggleDecrease = () => {
    dispatch(cartActions.removeItemFromCart(id));
  };
  return (
    <li className={classes.item}>
      <header>
        <h3>{name}</h3>
        <div className={classes.price}>
          ${totalPrice.toFixed(2)}{" "}
          <span className={classes.itemprice}>(${price.toFixed(2)}/item)</span>
        </div>
      </header>
      <div className={classes.details}>
        <div className={classes.quantity}>
          x <span>{quantity}</span>
        </div>
        <div className={classes.actions}>
          <button onClick={toggleDecrease}>-</button>
          <button onClick={toggleIncrease}>+</button>
        </div>
      </div>
    </li>
  );
};

export default CartItem;
