import { useSelector } from "react-redux";
import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import { useEffect } from "react";
import { REACT_APP_FIREBASE_AUTH_TOKEN } from "../secret";
function App() {
  const isCartVisible = useSelector((state) => state.ui.cartVisible);
  const cart = useSelector((state) => state.cart);
  useEffect(() => {
    fetch(`https://react-redux-practice-b9302-default-rtdb.firebaseio.com/cart.json?auth=${REACT_APP_FIREBASE_AUTH_TOKEN}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(cart),

    });
  }, [cart]);


  return (
    <Layout>
      {isCartVisible && <Cart />}
      <Products />
    </Layout>
  );
}

export default App;
